import React, {Component} from "react";
import Testimonial from "./Testimonial.js";
import "./TestimonialsWrapper.scss";


var TestimonialsArr = [
    {
        index : 1,
        image : "./img/girl1.jpg",
        name : 'Ania',
        rating : 5,
        testimonial_text : 'Najlepsze wyjazdy, cudowni ludzie, niepowtarzalne wspomnienia!'
    },

    {
        index : 2,
        image : './img/man1.jpg',
        name : 'Jacek',
        rating : 4,
        testimonial_text : 'Wszystko ok!'
        },

    {
        index : 3,
        image : './img/man2.jpg',
        name : 'Robert',
        rating : 3,
        testimonial_text : 'Autokar smierdzial i dlatego 3!'
        },
    {
        index : 4,
        image : './img/man2.jpg',
        name : 'Marian',
        rating : 5,
        testimonial_text : 'Zajebongo!'
        },
    {
        index : 5,
        image : './img/man1.jpg',
        name : 'Januszo',
        rating : 2,
        testimonial_text : 'Najgorsze biuro EVER!'
        }
    ];

function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
}
function GenerateTestimonialsArr() {
    var arr = [];
    while(arr.length < 3){
        var r = Math.floor(Math.random() * 100) + 1;
        if(arr.indexOf(r) === -1) {
            TestimonialsArr.forEach((el) => {
                if (el.index === r) {
                    arr.push(el)
                }
            })
        }
    }
}


