class TestimonialsWrapper extends Component {
    render () {
        return (
            <div className='Testimonials_Wrapper'>
                    {GenerateTestimonialsArr().map((el) =>
                        <Testimonial image={el.image} name={el.name} rating={el.rating} text={el.testimonial_text}/>
                    )}
